/**
 * # Opdracht
 * Class representing an assignment
 * ```typescript
 * const opdracht = new Opdracht(1, "Opdracht 1", "Dit is opdracht 1", 10, "Categorie 1", localStorage.getItem("token"), localStorage.getItem("uid")));
 * ```
 * @param id
 * @param naam
 * @param omschrijving
 * @param punten
 * @param categorie
 * @param token
 * @returns
 * @example
 */
export default class Opdracht {
    id: number;
    naam: string;
    omschrijving: string;
    punten: number;
    categorie: string;
    token: string;
    db: any;
    userId: string;
    flag: string;
    constructor(id: number, naam: string, omschrijving: string, punten: number, categorie: string, token: string, userId: string);
    /**
     * # val
     * Return up-to-date value of the given identifier
     * @param identifier
     * @returns
     */
    val(identifier: string): string | number;
    /**
     * # runSQL
     * Run SQL query on the database
     * ```typescript
     * opdracht.runSQL("SELECT * FROM opdrachten;");
     * ```
     * @param query
     * @returns
     */
    runUnsafeSQL(query: string, setup: string): Promise<any>;
    /**
     * # getFlag
     * Get the flag of the assignment
     * ```typescript
     * await opdracht.getFlag();
     * ```
     * Standard key for development: `0123456789ABCDEFFEDCBA987654321089ABCDEF01234567`
     * @returns Promise<string>
     */
    getFlag(): Promise<string>;
}
